import { ComponentFixture, TestBed } from '@angular/core/testing';

import { ShowallTodosComponent } from './showall-todos.component';

describe('ShowallTodosComponent', () => {
  let component: ShowallTodosComponent;
  let fixture: ComponentFixture<ShowallTodosComponent>;

  beforeEach(async () => {
    await TestBed.configureTestingModule({
      declarations: [ ShowallTodosComponent ]
    })
    .compileComponents();
  });

  beforeEach(() => {
    fixture = TestBed.createComponent(ShowallTodosComponent);
    component = fixture.componentInstance;
    fixture.detectChanges();
  });

  it('should create', () => {
    expect(component).toBeTruthy();
  });

  it('should be created', () => {
    expect(component).toBeTruthy();
  });



  describe('Add button', () => {

    it('should have a click event on add button', () => {

      spyOn(component, 'addClick');
      component.addClick();

      fixture.detectChanges();

      let buton = fixture.debugElement.query(By.css('button')).nativeElement.click();
      expect(component.addClick).toHaveBeenCalled();

    });

    it('should have a add button', () => {
      const addButton = fixture.debugElement.nativeElement.querySelector('#addButton')
      expect(addButton).toBeTruthy();
    });

  });  
});
